from langflow.custom.custom_component.component import Component
from langflow.io import MessageTextInput, Output
from langflow.schema.data import Data
import json

class ErrorMessageRAG(Component):
    display_name = "Error Message RAG"
    description = "If input matches a specific error, returns 'RAG will answer', else passes data"
    icon = "alert-circle"
    name = "ErrorMessageRAG"

    inputs = [
        MessageTextInput(
            name="input_data",
            display_name="Input Data",
            info="Connect output of previous node here",
            value="",  
            tool_mode=True,
        )
    ]

    outputs = [
        Output(display_name="Output", name="output", method="build_output")
    ]

    def build_output(self) -> Data:
        try:
            input_value = self.input_data

            try:
                parsed_input = json.loads(input_value)
            except (json.JSONDecodeError, TypeError):
                parsed_input = input_value

            input_str = str(parsed_input).lower()

            if [] in input_str:
                return Data(value="RAG will answer")


            return Data(value=parsed_input)

        except Exception as e:
            return Data(value=f"Error in ErrorMessageRAG: {str(e)}")
